# @st.cache_data()
def get_img_as_base64(url):
    respose = requests.get(url)
    img = Image.open(BytesIO(respose.content))
    buffered = BytesIO()
    img.save(buffered, format="JPEG")
    buffered.seek(0)
    img_str = base64.b64encode(buffered.getvalue()).decode()
    return img_str


